// Components
const ApplicationCard = (props) => {
  return (
    <Cell
      style={{
        color: "blue",
        paddingRight: "100px",
      }}
      col={3}
    >
      {props.app_name}
      <br />
      <h4>Description</h4>
      <br />
      {props.desc}
      <br />
      <Button>Setup Steps</Button>
    </Cell>
  );
};

class ApplicationCards extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      setup_steps: "Step1",
    };
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick() {
    this.setState({
      setup_steps: "Step2",
    });
  }
  render() {
    return (
      <div>
        <button onClick={this.handleClick}>God Got This!</button>
        <h1>{this.state.setup_steps}</h1>
      </div>
    );
  }
}

class SetupStepsModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      test: "Javay",
    };
  }
  render() {
    return (
      <div>
        <h1 style={{ color: "red" }}>{this.state.test}</h1>
        {this.props.app_name}
      </div>
    );
  }
}

